@Injectable()
export class AuthService {

    constructor(private userService: UsersService, private jwtService: JwtService) {
    }

    async login(userDto: CreateUserDto){
        // –ü—Ä–æ–≤–µ—Ä—è–µ–º –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è –Ω–∞ –Ω–∞–ª–∏—á–∏–µ –≤ –ë–î –∏ –ø—Ä–∞–≤–∏–ª—å–Ω–æ—Å—Ç–∏ –ø–∞—Ä–æ–ª—è
        const user = await this.validateUser(userDto)
        // –ü–æ—Ç–æ–º –µ—Å–ª–∏ –º—ã –ø–æ–ª—É—á–∞–µ–º –∞–≤—Ç–æ—Ä–∏–∑–∏—Ä–æ–≤–∞–Ω–Ω–æ–≥–æ –ø–æ–ª—å–∑–æ–≤–∞–µ—Ç–ª—è —Ç–æ –≤–æ–∑–≤—Ä–∞—â–∞–µ–º jwt —Ç–æ–∫–µ–Ω
        return this.generateToken(user)
    }

    async registration(userDto: CreateUserDto){
        const candidate = await this.userService.getUsersByEmail(userDto.email)
        if(candidate){
            // –≠—Ç–æ –∫–ª–∞—Å—Å –æ—à–∏–±–∫–∏ –∫–æ—Ç–æ—Ä—ã–π –ø—Ä–∏–Ω–∏–º–∞–µ—Ç –≤ —Å–µ–±—è —Å–æ–æ–±—â–µ–Ω–∏–µ –æ–± –æ—à–∏–±–∫–µ + —Å—Ç–∞—Ç—É—Å –æ—à–∏–±–∫–∏
            throw new HttpException('–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å —Å —Ç–∞–∫–∏–º email —Å—É—â–µ—Å—Ç–≤—É–µ—Ç', HttpStatus.BAD_REQUEST)
        }
        // –•–µ—à–∏—Ä—É–µ–º –ø–∞—Ä–æ–ª—å. –ü–µ—Ä–≤—ã–π –ø–∞—Ä–∞–º–µ—Ç—Ä –ø–µ—Ä–µ–¥–∞–µ–º –ø–∞—Ä–æ–ª—å –∞ –≤—Ç–æ—Ä—ã–º —Å—Ç–µ–ø–µ–Ω—å —Ö–µ—à–∏—Ä–æ–≤–∞–Ω–∏—è
        const hashPassword = await bcrypt.hash(userDto.password, 5)
        // –°–æ–∑–¥–∞–µ–º –∑–∞–ø–∏—Å—å –≤ —Ç–∞–±–ª–∏—Ü–µ –∏ –ø–µ—Ä–µ–¥–∞–µ–º –≤ –∫–∞—á–µ—Å—Ç–≤–µ —Å–≤–æ–π—Å—Ç–≤–∞ –ø–∞—Ä–æ–ª—å, —Ö–µ—à–∏—Ä–æ–≤–∞–Ω–Ω–æ–µ –∑–Ω–∞—á–µ–Ω–∏–µ
        const user = await this.userService.createUser({...userDto, password: hashPassword})
        // –í–æ–∑–≤—Ä–∞—â–∞–µ–º —Å–≥–µ–Ω—Ä–∏—Ä–æ–≤–∞–Ω–Ω—ã–π —Ç–æ–∫–µ–Ω
        return this.generateToken(user)
    }
    // –§—É–Ω–∫—Ü–∏—è –¥–ª—è –≥–µ–Ω–µ—Ä–∞—Ü–∏–∏ —Ç–æ–∫–µ–Ω–æ–≤  –ø—Ä–∏–Ω–∏–º–∞–µ—Ç –≤ —Å–µ–±—è –¥–∞–Ω–Ω—ã–µ –æ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–∏
    private async generateToken(user: User) {
        // —Å–æ–∑–¥–∞–µ–º –æ–±—ä–µ–∫—Ç —Å–æ –∑–Ω–∞—á–µ–Ω–∏—è–º–∏ –∫–æ—Ç–æ—Ä—ã–µ –±—É–¥–µ–º –≤–∫–ª–∞–¥—ã–≤–∞—Ç—å –≤ —Ç–æ–∫–µ–Ω
        const payload = {email: user.email, id: user.id, roles: user.roles}
        // –í–æ–∑–≤—Ä–∞—â–∞–µ–º –æ–±—ä–µ–∫—Ç —Å —Å–≥–µ–Ω–µ—Ä–∏—Ä–æ–≤–∞–Ω—ã–º —Ç–æ–∫–µ–Ω–æ–º
        return {
            token: this.jwtService.sign(payload)
        }
    }
//–§—É–Ω–∫—Ü–∏—è –ø—Ä–æ–≤–µ—Ä–∫–∏ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
    private async validateUser(userDto: CreateUserDto) {
        // –ü–æ–ª—É—á–∞–µ–º –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è –ø–æ –ø–æ—á—Ç–µ
        const user = await this.userService.getUsersByEmail(userDto.email)
        // –ü—Ä–æ–≤–µ—Ä—è–µ–º –ø–∞—Ä–æ–ª–∏ —Å –ë–î –∏ –∫–æ—Ç–æ—Ä—ã–µ –Ω–∞–º –ø—Ä–∏—à–ª–∏ —Å —Ñ—Ä–æ–Ω—Ç–∞
        const passwordEquals = await bcrypt.compare(userDto.password, user.password)
        // –ï—Å–ª–∏ —ç—Ç–æ –≤—Å–µ —Å–æ–≤–ø–∞–¥–∞–µ—Ç —Ç–æ –≤–æ–∑–≤—Ä–∞—â–∞–µ–º –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
        if(user && passwordEquals){
            return user;
        }
        throw new UnauthorizedException({message: "–ù–µ–∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã–π –µ–º–∞–π–ª –∏–ª–∏ –ø–∞—Ä–æ–ª—å"})
    }
}
```

