import {Dispatch} from "redux";
import axios from "axios";
// –≠–∫—à–µ–Ω –ø–æ–ª—É—á–µ–Ω–∏—è –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π
export const fetchUser = () => {
    // —á–µ—Ä–µ–∑ redux-thunk –≤—ã–∑—ã–≤–∞–µ–º dispatch —Å –ø—Ä–∏—Å–≤–æ–µ–Ω–∏—è –µ–º—É —Ç–∏–ø–∞ –∏ –ø–µ—Ä–µ–¥–∞—á–µ–π –≤ –Ω–µ–≥–æ —Ç–∏–ø–∞ —ç–∫—à–µ–Ω–∞
    return async (dispatch: Dispatch<UserAction>) => {
        // –ø–∞–∫—É–µ–º –≤ try catch –∏–±–æ —Ñ—É–Ω–∫—Ü–∏—è –∞—Å–∏–Ω—Ö—Ä–æ–Ω–Ω–∞—è
        try {
            // –≤—ã–∑—ã–≤–∞–µ–º dispatch –∫–æ—Ç–æ—Ä—ã–π —Å—Ç–∞–∏—Ç –∑–∞–≥—Ä—É–∑—á–∏–∫
            dispatch({type: UserActionTypes.FETCH_USERS})
            // –¥–µ–ª–∞–µ–º –∑–∞–ø—Ä–æ—Å –Ω–∞ —Å–µ—Ä–≤–µ—Ä
            const response = await axios.get('https://jsonplaceholder.typicode.com/users')
            // –ø–æ–ª—É—á–µ–Ω–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ –ø—Ä–µ–¥–∞–µ–º –≤ dispatch –∫–æ—Ç–æ—Ä—ã–π –≤—ã–∑—ã–≤–∞–µ—Ç action –ø–æ —Ç–∏–ø—É –∏ —Ç–∞–∫ –∂–µ
            // –ø–µ—Ä–µ–¥–∞–µ–º —Ç—É–¥–∞ –¥–∞–Ω—ã–µ
            dispatch({type: UserActionTypes.FETCH_USERS_SUCCESS, payload: response.data})
