const rect5: RectWithArea = {
    id: '123',
    size: {
        width: 20,
        height: 20
    },
    // –°–∞–º–æ–µ –∏–Ω—Ç–µ—Ä–µ—Å–Ω–æ–µ —Ç–∏–ø –º–µ—Ç–æ–¥—É –∑–∞–¥–∞–µ—Ç—Å—è –∫–∞–∫ –≤ –∏–Ω—Ç–µ—Ä—Ñ–µ–π—Å–µ —Ç–∞–∫ –∏ –≤ –æ–±—ä–µ–∫—Ç–µ
    getArea(): number {
