## –ü—Ä–∏–º–µ—Ä

``` js
// –°–µ—Ä–≤–∏—Å–∞—Ö –º–µ —Ä–µ–∞–ª–∏–∑—É–µ–º —á–∏—Å—Ç–æ –ª–æ–≥–∏–∫—É —Ä–∞–±–æ—Ç—ã —Å –¥–∞–Ω–Ω—ã–º–∏ –Ω–µ –∫–∞—Å–∞—è—Å—å –∑–∞–ø—Ä–æ—Å–æ–≤ –∏ –æ—Ç–≤–µ—Ç–æ
import Post from "./Post.js";
import fileService from "./fileService.js";

class PostService {
    // –°–æ–∑–¥–∞–µ–º —Ñ—É–Ω–∫—Ü–∏—é –¥–ª—è —Å–æ–∑–¥–∞–Ω–∏—è –ø–æ—Å—Ç–æ–≤, –Ω–æ —ç—Ç–æ –º–æ–∂–µ—Ç –±—ã—Ç—å –ª—é–±–∞—è –¥—Ä—É–≥–∞—è —Ñ—É–Ω–∫—Ü–∏—è –æ–±—Ä–∞–±–∞—Ç—ã–≤–∞—é—â–∞—è –∑–∞–ø—Ä–æ—Å –æ—Ç–≤–µ—Ç
    async create(post, picture) {
        //–°–æ—Ö—Ä–∞–Ω—è–µ–º —Ñ–∞–π–ª
        const fileName = fileService.saveFile(picture)
            // –¢—É—Ç –º—ã –∏—Ö –ø–µ—Ä–µ–¥–∞–µ–º –≤ –º–æ–¥–µ–ª—å, –≤—Å–µ –æ–ø–µ—Ä–∞—Ü–∏–∏ —Å –±–∞–∑–æ–π –¥–∞–Ω–Ω–æ–π –∞—Å–∏–Ω—Ö—Ä–æ–Ω–Ω—ã–µ, –ø–æ—ç—Ç–æ–º—É await –æ–±—è–∑–∞—Ç–µ–ª–µ–Ω
        // –∏ –Ω–∞ —Ö–æ–¥—É –¥–æ–±–∞–≤–ª—è–µ–º –∏–º—è —Ñ–∞–π–ª–∞ –∫ –ø–æ—Å—Ç—É –≤ –ë–î
        const createdPost = await Post.create({...post, picture: fileName})
            return  createdPost
    }

    async getAll() {
        // –ù–∞—Ö–æ–¥–∏–º –∏ –æ—Ç–¥–∞–µ–º –ø–æ—Å—Ç—É
            const posts = await Post.find()
            return posts
    }

    async getOne(id) {
            // –î–µ–ª–∞–µ–º –ø—Ä–æ–≤–µ—Ä–∫—É, –ø–µ—Ä–µ–¥–∞–Ω –ª–∏ id –≤ –∑–∞–ø—Ä–æ—Å–µ
            if (!id){
                throw new Error('–Ω–µ —É–∫–∞–∑–∞–Ω ID')
            }
            // –í—ã–∑—ã–≤–∞–µ–º —Ñ—É–Ω–∫—Ü–∏—é –ø–æ–∏—Å–∫–∞ –ø–æ id
            const post = await Post.findById(id)
            // –ò –≤–æ–∑–≤—Ä–∞—â–∞–µ–º –ø–æ—Å—Ç –≤ –æ—Ç–≤–µ—Ç–µ
            return post
    }

    async update(post) {
            // –ü—Ä–æ–≤–µ—Ä—è–µ–º –ø–æ—Å—Ç –Ω–∞ id, –∫–æ—Ç–æ—Ä—ã–π –Ω–∞–º –Ω—É–∂–µ–Ω —á—Ç–æ –±—ã –Ω–∞–π—Ç–∏ —Å—Ç–∞—Ä—É—é –≤–µ—Ä—Å–∏—é –ø–æ—Å—Ç–∞ –≤ –ë–î
            if (!post._id){
                // –í–æ–∑–≤—Ä–∞—â–∞–µ–º –∫–ª–∏–µ–Ω—Ç—É –æ—Ç–≤–µ—Ç  —Å–æ–æ–±—â–µ–Ω–∏–µ —Å —É–∫–∞–∑–∞–Ω–∏–µ–º –ø—Ä–∏—á–∏–Ω—ã –æ—à–∏–±–∫–∏
                    throw new Error('–Ω–µ —É–∫–∞–∑–∞–Ω ID')
            }
            // –¢—É—Ç –∏—Å–ø–æ–ª—å–∑—É–µ–º —Ñ—É–Ω–∫—Ü–∏—é –¥–ª—è –ø–æ–∏—Å–∫–∞ –∏ –∏–∑–º–µ–Ω–Ω–∏—è, –≤ –∫–æ—Ç–æ—Ä—É—é –ø–µ—Ä–µ–¥–∞–µ–º id —á—Ç–æ –±—ã –Ω–∞–π—Ç–∏ –ø–æ—Å—Ç –≤ –ë–î,
            // –¥–∞–ª–µ–µ –ø–µ—Ä–µ–¥–∞–µ–º –Ω–æ–≤—É—é –≤–µ—Ä—Å–∏—é –ø–æ—Å—Ç–∞ –∏ 3-–∏–º –∞—Ä–≥—É–º–µ–Ω—Ç–æ–º –ø–æ–¥—Ç–∏–≤–µ—Ä–∂–¥–∞–µ–º —á—Ç–æ –º—ã —Ö–æ—Ç–∏–º –∏–∑–º–µ–Ω–∏—Ç—å –∑–∞–ø–∏—Å—å –≤ –ë–î
            const updatedPost = await Post.findByIdAndUpdate(post._id, post, {new: true})
            // –ò–≤–æ–∑–≤—Ä–∞—â–∞–µ–º –æ–±–Ω–æ–≤–ª–µ–Ω–Ω—É—é –≤–µ—Ä—Å–∏—é –æ–±—Ä–∞—Ç–Ω–æ
            return updatedPost
    }

    async delete(id) {
            // –î–µ–ª–∞–µ–º –ø—Ä–æ–≤–µ—Ä–∫—É, –ø–µ—Ä–µ–¥–∞–Ω –ª–∏ id –≤ –∑–∞–ø—Ä–æ—Å–µ
            if (!id){
                // –í–æ–∑–≤—Ä–∞—â–∞–µ–º –∫–ª–∏–µ–Ω—Ç—É –æ—Ç–≤–µ—Ç —Å —Å—Ç–∞—Ç—É—Å–æ–º (–æ—à–∏–±–∫–∞ –∏–∑-–∑–∞ —Å–∏–Ω—Ç–∞–∫—Å–∏—Å–∞ –∑–∞–ø—Ä–æ—Å–∞) –∞ —Ç–∞–∫ –∂–µ
                // —Å–æ–æ–±—â–µ–Ω–∏–µ —Å —É–∫–∞–∑–∞–Ω–∏–µ–º –ø—Ä–∏—á–∏–Ω—ã –æ—à–∏–±–∫–∏
                throw new Error('–Ω–µ —É–∫–∞–∑–∞–Ω ID')
            }
            // –í—ã–∑—ã–≤–∞–µ–º —É –º–æ–¥–µ–ª–∏ —Ñ—É–Ω–∫—Ü–∏—é –¥–ª—è –ø–æ–∏—Å–∫–∞ –∑–∞–ø–∏—Å–∏ –≤ –ë–î –ø–æ id –∏ —É–¥–∞–ª–µ–Ω–∏—è
            const post = await Post.findByIdAndDelete(id)
            return post
    }
}

export default new PostService()
```

