function useScript(src) {
  //–°–æ–∑–¥–∞–µ–º —Å—Ç—ç–π—Ç –¥–ª—è —Ö—Ä–∞–Ω–µ–Ω–∏—è —Å—Ç–∞—Ç—É—Å–æ–≤ –∑–∞–ø—Ä–æ—Å–æ–≤ ("idle", "loading", "ready", "error")
  const [status, setStatus] = useState(src ? "loading" : "idle");
