    () => {
      // –ï—Å–ª–∏ src –ø—É—Å—Ç–æ–µ, —É—Å—Ç–∞–Ω–æ–≤–∏—Ç—å —Å—Ç–∞—Ç—É—Å "–±–µ–∑–¥–µ–π—Å—Ç–≤–∏–µ" –∏ –≤—ã–π–¥–∏ –∏–∑ —Ö—É–∫–∞
      // 
      if (!src) {
        setStatus("idle");
        return;
      }
      // –ü–æ–ª—É—á–∞–µ–º —ç–ª–µ–º–µ–Ω—Ç —Å–∫—Ä–∏–ø—Ç–∞ —Å –ø–æ–º–æ—â—å—é src
      let script = document.querySelector(`script[src="${src}"]`);
      // –µ—Å–ª–∏ —Ç–∞–∫–æ–≥–æ –Ω–µ—Ç —Ç–æ –º—ã —Å–æ–∑–¥–∞–¥–∏–º))
      if (!script) {
        // –°–û–ó–î–ê–ï–ú
        script = document.createElement("script");
        script.src = src;
        script.async = true;
        script.setAttribute("data-status", "loading");
        // –î–æ–±–∞–≤–ª—è–µ–º
        document.body.appendChild(script);
        // –≠—Ç–∞ —Ñ—É–Ω–∫—Ü–∏—è –æ—Ç—Å–ª–µ–∂–∏–≤–∞–µ—Ç —Å–æ—Å—Ç–æ—è–Ω–∏–µ –∑–∞–≥—Ä—É–∑–∫–∏ —Å —Ä–µ—Å—É—Ä—Å–∞
        const setAttributeFromEvent = (event) => {
          script.setAttribute(
            "data-status",
            event.type === "load" ? "ready" : "error"
          );
        };
        script.addEventListener("load", setAttributeFromEvent);
        script.addEventListener("error", setAttributeFromEvent);
      } else {
        // –ü–æ–ª—É—á–∞–µ–º —Å—É—â–µ—Å—Ç–≤—É—é—â–∏–π —Å—Ç–∞—Ç—É—Å —Å–∫—Ä–∏–ø—Ç–∞ –∏–∑ –∞—Ç—Ä–∏–±—É—Ç–∞ –∏ —É—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –µ–≥–æ –≤ —Å–æ—Å—Ç–æ—è–Ω–∏–µ.
        setStatus(script.getAttribute("data-status"));
      }
      // –û–±—Ä–∞–±–æ—Ç—á–∏–∫ —Å–æ–±—ã—Ç–∏–π —Å–∫—Ä–∏–ø—Ç–∞ –¥–ª—è –æ–±–Ω–æ–≤–ª–µ–Ω–∏—è —Å—Ç–∞—Ç—É—Å–∞ –≤ —Å–æ—Å—Ç–æ—è–Ω–∏–∏
      // –ü—Ä–∏–º–µ—á–∞–Ω–∏–µ. –î–∞–∂–µ –µ—Å–ª–∏ —Å—Ü–µ–Ω–∞—Ä–∏–π —É–∂–µ —Å—É—â–µ—Å—Ç–≤—É–µ—Ç, –Ω–∞–º –≤—Å–µ —Ä–∞–≤–Ω–æ –Ω—É–∂–Ω–æ –¥–æ–±–∞–≤–∏—Ç—å –æ–±—Ä–∞–±–æ—Ç—á–∏–∫–∏ —Å–æ–±—ã—Ç–∏–π –¥–ª—è –æ–±–Ω–æ–≤–ª–µ–Ω–∏—è —Å–æ—Å—Ç–æ—è–Ω–∏—è –¥–ª—è * —ç—Ç–æ–≥–æ * —Ö—É–∫–∞.
      const setStateFromEvent = (event) => {
        setStatus(event.type === "load" ? "ready" : "error");
      };
      // Add event listeners
      script.addEventListener("load", setStateFromEvent);
      script.addEventListener("error", setStateFromEvent);
      // –£–±–∏—Ä–∞–µ–º –æ–±—Ä–∞–±–æ—Ç—á–∏–∫ –ø—Ä–∏ —Ä–∞–∑–º–æ–Ω—Ç–∏—Ä–æ–≤–∞–Ω–∏–∏ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–∞
      return () => {
