  // –ú–∞—Å—Å–∏–≤ –∑–Ω–∞—á–µ–Ω–∏–π –ø—Ä–µ–¥—ã–¥—É—â–µ–≥–æ —Å–æ—Å—Ç–æ—è–Ω–∏—è –æ–±–Ω–æ–≤–ª—è–µ—Ç—Å—è –∫–∞–∂–¥—ã–π —Ä–∞–∑, –∫–æ–≥–¥–∞ –º—ã –Ω–∞–∂–∏–º–∞–µ–º –Ω–æ–≤–æ–µ —Å–æ—Å—Ç–æ—è–Ω–∏–µ
  past: [],
  // –¢–µ–∫—É—â–µ–µ –∑–Ω–∞—á–µ–Ω–∏–µ —Å–æ—Å—Ç–æ—è–Ω–∏—è
  present: null,
  // –ë—É–¥–µ—Ç —Å–æ–¥–µ—Ä–∂–∞—Ç—å "–±—É–¥—É—â–∏–µ" –∑–Ω–∞—á–µ–Ω–∏—è —Å–æ—Å—Ç–æ—è–Ω–∏—è, –µ—Å–ª–∏ –º—ã –æ—Ç–º–µ–Ω–∏–º (—á—Ç–æ–±—ã –º—ã –º–æ–≥–ª–∏ –ø–æ–≤—Ç–æ—Ä–∏—Ç—å)
  future: [],
};
// reducer –¥–ª—è –æ–±—Ä–∞–±–æ—Ç–∫–∏ –∏–∑–º–µ–Ω–µ–Ω–∏–π —Å–æ—Å—Ç–æ—è–Ω–∏—è –Ω–∞ –æ—Å–Ω–æ–≤–µ –¥–µ–π—Å—Ç–≤–∏—è
const reducer = (state, action) => {
  const { past, present, future } = state;
  switch (action.type) {
    case "UNDO":
      const previous = past[past.length - 1];
      const newPast = past.slice(0, past.length - 1);
      return {
        past: newPast,
        present: previous,
        future: [present, ...future],
      };
    case "REDO":
      const next = future[0];
      const newFuture = future.slice(1);
      return {
        past: [...past, present],
        present: next,
        future: newFuture,
      };
    case "SET":
      const { newPresent } = action;
      if (newPresent === present) {
        return state;
      }
      return {
        past: [...past, present],
        present: newPresent,
        future: [],
      };
    case "CLEAR":
      const { initialPresent } = action;
      return {
        ...initialState,
        present: initialPresent,
      };
  }
};
// C–æ–∑–¥–∞–µ–º —Ñ—É–Ω–∫—Ü–∏—é –∫–æ—Ç–æ—Ä–∞—è –±—É–¥–µ—Ç –ø—Ä–∏–Ω–∏–º–∞—Ç—å –≤ —Å–µ–±—è –Ω–∞—Å—Ç–æ—è—â–µ–µ —Å–æ—Å—Ç–æ—è–Ω–∏–µ
const useHistory = (initialPresent) => {
  // –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∏—Ä—É–µ–º —Å–æ—Å—Ç–æ—è–Ω–∏—è –∏ —Ä–µ–¥—å—é—Å–µ—Ä 
  // –∏ —Ç–∞–∫ –∂–µ —É—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –Ω—ã–Ω–µ—à–µ–Ω–Ω–µ–µ —Å–æ—Å—Ç–æ—è–Ω–∏–µ
  const [state, dispatch] = useReducer(reducer, {
    ...initialState,
    present: initialPresent,
  });
  // –°—á–µ—Ç—á–∏–∫–∏ –ø–æ–≤—Ç–æ—Ä–∞ –∏ –æ—Ç–º–µ–Ω—ã
  const canUndo = state.past.length !== 0;
  const canRedo = state.future.length !== 0;
  // 
  const undo = useCallback(() => {
    if (canUndo) {
      dispatch({ type: "UNDO" });
    }
  }, [canUndo, dispatch]);
  const redo = useCallback(() => {
    if (canRedo) {
      dispatch({ type: "REDO" });
    }
  }, [canRedo, dispatch]);
  const set = useCallback(
    (newPresent) => dispatch({ type: "SET", newPresent }),
    [dispatch]
  );
  const clear = useCallback(() => dispatch({ type: "CLEAR", initialPresent }), [
    dispatch,
  ]);
  // –î–∞–Ω–Ω—ã–π —Ö—É–∫ –≤–æ–∑–≤—Ä–∞—â–∞–µ—Ç –Ω—ã–Ω–µ—à–Ω–µ–µ —Å–æ—Å—Ç–æ—è–Ω–∏–µ, —É—Å—Ç–∞–Ω–æ–≤–∫—É –Ω–æ–≤–æ–≥–æ, –æ—Ç–º–µ–Ω—É, –ø–æ–≤—Ç–æ—Ä, –æ—á–∏—Å—Ç–∫—É, –∞ —Ç–∞–∫ –∂–µ —Å—á–µ—Ç—á–∏–∫–∏ –ø–æ–≤—Ç–æ—Ä–∞ –∏ –æ—Ç–º–µ–Ω—ã.
  return { state: state.present, set, undo, redo, clear, canUndo, canRedo };
};
