  const getValue = () => {
    //–ü–æ–ª—É—á–∏—Ç—å –∏–Ω–¥–µ–∫—Å –ø–µ—Ä–≤–æ–≥–æ —Å–æ–≤–ø–∞–¥–∞—é—â–µ–≥–æ —Å –Ω—ã–Ω–µ—à–Ω–∏–º —Ä–∞–∑—Ä–µ—à–µ–Ω–∏–µ–º –º–µ–¥–∏–∞-–∑–∞–ø—Ä–æ—Å–æ–º
    //—ç—Ç–æ –ø—Ä–æ–≤–µ—Ä—è–µ—Ç—Å—è —Å –ø–æ–º–æ—â—å—é mql.matches, –∫–æ—Ç–æ—Ä–∞—è –≤–æ–∑–≤—Ä–∞—â–∞–µ—Ç true –µ—Å–ª–∏ –º–µ–¥–∏–∞–∑–∞–ø—Ä–æ—Å —Å–æ–æ—Ç–≤–µ—Ç—Å–≤—É–µ—Ç —Ä–∞–∑—Ä–µ—à–µ–Ω–∏—é
    const index = mediaQueryLists.findIndex((mql) => mql.matches);
    // –í–µ—Ä–Ω—É—Ç—å –∑–Ω–∞—á–µ–Ω–∏–µ –∏–Ω–¥–µ–∫—Å—É –∫–æ—Ç–æ—Ä–æ–≥–æ —Å–æ–æ—Ç–≤–µ—Ç—Å–≤—É–µ—Ç –∏–Ω–¥–µ–∫—Å –∞–∫—É—Ç–∞–ª—å–Ω–æ–≥–æ –º–µ–¥–∏–∞–∑–∞–ø—Ä–æ—Å–∞ –µ—Å–ª–∏ —Ç–∞–∫–æ–≤–æ–≥–æ –Ω–µ—Ç —Ç–æ –≤–æ–∑–≤—Ä–∞—â–∞–µ–º –∑–Ω–∞—á–µ–Ω–∏–µ –ø–æ —É–º–æ–ª—á–∞–Ω–∏—é 
