      return () =>
        mediaQueryLists.forEach((mql) => mql.removeListener(handler));
    },
    [] // –ü—É—Å—Ç–æ–π –º–∞—Å—Å–∏–≤ –≥–∞—Ä–∞–Ω—Ç–∏—Ä—É–µ—Ç, —á—Ç–æ —ç—Ñ—Ñ–µ–∫—Ç –±—É–¥–µ—Ç –≤—ã–ø–æ–ª–Ω—è—Ç—å—Å—è —Ç–æ–ª—å–∫–æ –ø—Ä–∏ –º–æ–Ω—Ç–∏—Ä–æ–≤–∞–Ω–∏–∏ –∏ —Ä–∞–∑–º–æ–Ω—Ç–∏—Ä–æ–≤–∞–Ω–∏–∏.
  );
  //–í–æ–∑–≤—Ä–∞—â–∞–µ–º –∑–Ω–∞—á–µ–Ω–∏–µ –∫–æ—Ç–æ—Ä–æ–µ –∞–∫—Ç—É–∞–ª—å–Ω–æ –ø—Ä–∏ –¥–∞–Ω–Ω–æ–º —Ä–∞—Å—à–∏—Ä–µ–Ω–∏–∏
  return value;
}
