// –û–±—ä—è–≤–ª–µ–Ω–∏–µ –∫–æ–Ω—Ç–µ–∫—Å—Ç–∞
const AlertContext = React.createContext()
// –§—É–Ω–∫—Ü–∏—è –∫–æ—Ç–æ—Ä–∞—è –±—É–¥–µ—Ç –≤–æ–∑–≤–∞—Ä–∞—â–∞—Ç—å –¥–∞–Ω–Ω—ã–µ –∏–∑ –∫–æ–Ω—Ç–µ–∫—Å—Ç–∞
export const useAlert = () => {
  return useContext(AlertContext)
}
// –≠—Ç–æ –¥–ª—è —Ç–∏–ø–æ–≤ —ç–∫—à–µ–Ω–∞
const SHOW_ALERT = 'show'
const HIDE_ALERT = 'hide'
// –°–∞–º —Ä–µ–¥—é—Å–µ—Ä, –ø–æ —Å—É—Ç–∏ —Ç–æ–∂–µ —Å–∞–º–æ–µ —á—Ç–æ –∏ –≤ Redux
const reducer = (state, action) => {
  switch (action.type) {
    case SHOW_ALERT: return {...state, visible: true, text: action.text}
    case HIDE_ALERT: return {...state, visible: false}
    default: return state
  }
}

export const AlertProvider = ({ children }) => {
  // –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è useReducer –ø–µ—Ä–≤—ã–π —ç–ª–µ–º–µ–Ω—Ç —ç—Ç–æ –¥–∞–Ω–Ω—ã–µ, –≤—Ç–æ—Ä–æ–π —ç—Ç–æ
  // –ø–æ —Å—É—Ç–∏ —Ç–æ—Ç –∂–µ —Å–∞–º—ã–π –¥–∏—Å–ø–∞—Ç—á —á—Ç–æ –∏ –≤—Ä–µ–¥–∞–∫—Å–µ, useReducer –ø–µ—Ä–≤—ã–º 
  // –∞—Ä–≥—É–º–µ–Ω—Ç–æ–º –ø—Ä–∏–Ω–∏–º–∞–µ—Ç —Å–æ–∑–¥–∞–Ω–Ω—ã–π –≤—ã—à–µ —Ä–µ–¥—é—Å–µ—Ä –∞ –≤—Ç–æ—Ä—ã–º –æ–±—ä–µ–∫—Ç-—Ö—Ä–Ω–∞–∏–ª–∏—â–µ
  const [state, dispatch] = useReducer(reducer, {
    visible: false,
    text: ''
  })
//–°–æ–∑–¥–∞–Ω–∏–µ —Ñ—É–Ω–∫—Ü–∏–π –¥–ª—è –∏–∑–º–µ–Ω–Ω–µ–∏—è —Å–æ—Å—Ç–æ—è–Ω–∏—è –∫–æ–Ω—Ç–µ–∫—Å—Ç–∞, —Ç–∏–ø–∏—á–Ω—ã–µ —ç–∫—à–µ–Ω—ã
  //—Å–æ—Å—Ç–æ—è—Ç –∏–∑ –æ–±—ä—è–≤–ª–µ–Ω–Ω–æ–≥–æ —Ç–∏–ø–∞ —ç–∫—à–µ–Ω–∞ –∏ –µ—Å–ª–∏ –Ω–∞–¥–∞ —Ç–æ –ø—Ä–µ–¥–∞—á–∏ –∫–æ–Ω—Ç–µ–Ω—Ç–∞
  const show = text => dispatch({ type: SHOW_ALERT, text })
  const hide = () => dispatch({ type: HIDE_ALERT })

  return (
    <AlertContext.Provider value={{
      visible: state.visible,
      text: state.text,
      show, hide
    }}>
        { children }
    </AlertContext.Provider>
  )
}
```

