    depositStart[msg.sender] = 0;
    //–°–±—Ä–∞—Å—ã–≤–∞–µ–º –±–∞–ª–∞–Ω—Å –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è –Ω–∞ 0
  etherBalanceOf[msg.sender] = 0;
    //–°—Ç–∞–≤–∏–º —Å—Ç–∞—Ç—É—Å –¥–µ–ø–æ–∑–∏—Ç–∞ –Ω–µ–∞–∫—Ç–∏–≤–Ω—ã–º
