let initialState = {
  data: null,//–°—é–¥–∞ —Å –ø–æ–º–æ—â—å—é actions –ø—Ä–æ–∫–∏–¥—ã–≤–∞–µ—Ç—Å—è –∫–æ–Ω—Ç–µ–Ω—Ç, –µ—Å–ª–∏ –æ–Ω —Ç—É—Ç –µ—Å—Ç—å, –∑–Ω–∞—á–∏—Ç //data = true –∏ –æ–∫–Ω–æ –≤—ã–∑—ã–≤–∞–µ—Ç—Å—è, –µ—Å–ª–∏ –Ω–µ—Ç —Ç–æ data = null = false –∏ –æ–∫–Ω–æ –∑–∞–∫—Ä—ã–≤–∞–µ—Ç—Å—è  
}
//–ó–¥–µ—Å—å actions –æ–±—Ä–∞–±–∞—Ç—ã–≤–∞—é—Ç—Å—è
const ModalReducer = (state = initialState, action) => {
  switch(action.type){
    case OPEN_MODAL:
      return {
        ...state,
        data: action.data
      }

    case CLOSE_MODAL:
      return {
        ...state,
        data: null
      }
    default:
      return state;
  }
}


export default ModalReducer;
```

